# implicit catalog
import collections
class Solution(object):
    def findSubstringInWraproundString_t(self, p):
        """
        :type p: str
        :rtype: int
        """
        if len(p) ==  0:
            return 0
        # len(p).p()
        dp = [1 for _ in xrange(len(p))]
        b = bytearray(p,'utf8')
        cc = {p[0]:1}
        continue_count = 1
        for i in xrange(1,len(p)):
            i_in_cache =  cc.get(p[i],0)
            if i_in_cache == 0:
                dp[i] = dp[i-1] + 1
                cc[p[i]] = 1
                # cc.p()
            else:
                dp[i] = dp[i-1]
                

            if  b[i] - 1 == b[i-1] or (b[i-1]==122 and b[i]==97):
                for j in xrange(continue_count,0,-1):
                    if j >= cc[p[i-j]]:
                        dp[i] += 1
                        cc[p[i-j]] += 1

                continue_count += 1
                
            else:
                continue_count = 1
        return dp[len(p)-1]

    def findSubstringInWraproundString(self, p):
        """
        :type p: str
        :rtype: int
        """
        pattern = 'zabcdefghijklmnopqrstuvwxyz'
        cmap = collections.defaultdict(int)
        start = end = 0
        for c in range(len(p)):
            # p[c-1:c+1] not in pattern means not continued
            if c and p[c-1:c+1] not in pattern:
                (c,p[c-1:c+1],start, end).p()
                for x in range(start, end):
                    cmap[p[x]] = max(end - x, cmap[p[x]])
                start = c
            end = c + 1
        end.p()
        cmap.p()
        for x in range(start, end):
            cmap[p[x]] = max(end - x, cmap[p[x]])
            cmap[p[x]].p()
        return sum(cmap.values())    
    
